import React from "react";
import * as axios from "axios";

import s from './Users.module.css'
import userPhoto from '../../assets/images/images.png'

class UsersApiComponents extends  React.Component{
   componentDidMount(){
      axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response=>{        //отправляем get запрос на сервак .then(response(когда запрос выполниться пишем логики что нужно сделать)
      this.props.setUsers(response.data.items)                                   //это наш массив пользователей который отдает нам сервак
      this.props.setTotalUserCount(response.data.totalCount)  
   })
   }

   onPageChanged = (pageNumber) => {
      this.props.setCurrentPage(pageNumber)
      axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${ pageNumber }&count=${ this.props.pageSize }`).then(response=>{        //отправляем get запрос на сервак .then(response(когда запрос выполниться пишем логики что нужно сделать)
      this.props.setUsers(response.data.items) 
      })
   }

   render(){
      let pagesCount = Math.ceil( this.props.totalUsersCount / this.props.pageSize)
      let pages = []
      for (let i=1; i <= pagesCount; i++) {
         pages.push(i)
      }
      console.log(this.props)
      console.log(pages)

    return (

       <div className={s.users_wrap}>
         <div>
            {pages.map(p => {
               return (
                  <span 
                     className = { this.props.currentPage === p && s.selectedPage }
                     onClick = { (e) => { this.onPageChanged(p) }} 
                  > 
                     { p } 
                  </span> 

               )
               })}   
         </div>
          {
             this.props.users.map(user=><div key={user.id} className={s.users_items}>

                   <div>
                      <img src={user.photos.small !=null ? user.photos.small: userPhoto} className={s.userPhoto}/> 
                   </div>  

                   <div className={s.user_items_data}>
                      <div>
                         {user.followed
                            ? <button onClick={()=>{this.props.unfollow(user.id)}}>Follow</button>
                            : <button onClick={()=>{this.props.follow(user.id)}}>Unfollow</button>}
                      </div>

                      <div>
                         {user.name}   
                      </div>
                      <div>
                         {user.status}   
                      </div>


                      <div>
                         {'user.location.country'}   
                      </div>
                      <div>
                         {'user.location.city'}   
                      </div>
                   </div>

             </div>
             )
          }
       </div>
    )
}
}

 export default UsersApiComponents
