import React from "react";
import * as axios from "axios";

import s from './Users.module.css'
import userPhoto from '../../assets/images/images.png'

class Users extends  React.Component{
   componentDidMount(){
      axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response=>{        //отправляем get запрос на сервак .then(response(когда запрос выполниться пишем логики что нужно сделать)
      this.props.setUsers(response.data.items)                                   //это наш массив пользователей который отдает нам сервак
      })
   }
   render(){
      let pagesCount = this.props.totalUsersCount / this.props.pageSize
      let pages = []
      for (let i=1; i<=pagesCount; i++) {
         pages.push(i)
      }
      console.log(this.props)

    return (

       <div className={s.users_wrap}>
         <div>
            {pages.map(p => {
              return <span className={this.props.currentPage === p && s.selectedPage}>{p}</span>
            })}

         </div>
          {
             this.props.users.map(user=><div key={user.id} className={s.users_items}>

                   <div>
                      <img src={user.photos.small !=null ? user.photos.small: userPhoto} className={s.userPhoto}/> 
                   </div>  

                   <div className={s.user_items_data}>
                      <div>
                         {user.followed
                            ? <button onClick={()=>{this.props.unfollow(user.id)}}>Follow</button>
                            : <button onClick={()=>{this.props.follow(user.id)}}>Unfollow</button>}
                      </div>

                      <div>
                         {user.name}   
                      </div>
                      <div>
                         {user.status}   
                      </div>


                      <div>
                         {'user.location.country'}   
                      </div>
                      <div>
                         {'user.location.city'}   
                      </div>
                   </div>

             </div>
             )
          }
       </div>
    )
}
}

 export default Users
