import React from "react";
import { connect } from "react-redux";
import { followAC, setCurrentPageAC, setUsersAC, unfollowAC } from "../../redux/users-reducer";
import Users from "./Users";

let mapStateToProps=(state)=>{                 //берет весь state, а отдает через props тот о котором можно знать чистой функции
   return {
      users: state.usersPage.users,
      pageSize: state.usersPage.pageSize,
      totalUsersCount: state.usersPage.totalUserCount,
      currentPage: state.usersPage.currentPage
   }
}

let mapDispatchToProps=(dispatch)=>{
   return{
      follow:(userId)=>{                      //через пропсы передаем эшкн, через экшнкреейтор
         dispatch(followAC(userId))
      },
      unfollow:(userId)=>{
         dispatch(unfollowAC(userId))
      },
      setUsers: (users)=>{
         dispatch(setUsersAC(users))
      },
      setCurrentPage: (pageNumber) => {
         dispatch(setCurrentPageAC(pageNumber))
      },
      setTotalUserCount: (totalCount) => {
         dispatch(setTotalUserCountAC(totalCount))
      }
   }
}










export default connect(mapStateToProps, mapDispatchToProps)(Users)
